type Input     = (Input1, Input2)
type Input1    = [TestInstr]
type TestInstr = ([Val], MInstr, [Val])
type Input2    = [MInstr]

type MInstr    = (OpCode, (Int, Int, Int))
type OpCode    = Int

type OpCodeMap'= Map OpCode [Int]
type OpCodeMap = Map OpCode Int

-- ----------------------------------------

type Result1 = Int
type Result2 = Int

solve1 :: Input -> Result1
solve1 = length . filter ((>= 3) . length) . map (snd . testInstr) . fst

testInstr :: TestInstr -> (OpCode, [Int])    -- list of instr, which match
testInstr (before, (oc, (x1, x2, x3)), expected) =
  (oc,) . concatMap (uncurry runTest) $ allInstr
  where
    runTest :: Int -> Instr -> [Int]
    runTest opc ins
      | after == expected = [opc]
      | otherwise         = []
      where
        after = snd $ runMachine (ins x1 x2 x3) before

buildOpCodeMap :: Input1 -> OpCodeMap'
buildOpCodeMap = foldl' ins M.empty . map testInstr
  where
    ins m (oc, is) = M.insertWith intersect oc is m

solveConstraint :: [(OpCode, [Int])] -> [[(OpCode, Int)]]
solveConstraint [] = [[]]
solveConstraint ((c, is) : rest) =
  concatMap step is
  where
    step i
      | Just rest' <- mrest = map ((c, i) :) . solveConstraint $ rest'
      | otherwise           = []
      where
        mrest :: Maybe [(OpCode, [Int])]
        mrest = foldr remi (Just []) rest
          where
            remi (c', is') res
              | null is''      = Nothing
              | Nothing <- res = Nothing
              | otherwise      = ((c', is'') :) <$> res
                where
                  is'' = delete i is'


checkOpCodeMap :: [[(OpCode, Int)]] -> OpCodeMap
checkOpCodeMap [xs] = M.fromList xs
checkOpCodeMap []   = error "no unique opcode map found"
checkOpCodeMap xxs  = error $ "ambigious opcodemap " ++ show xxs

solve2 :: Input -> Result2
solve2 (ts, prg) = head . snd $ runMachine act m0
  where
    m0  = replicate 4 0
    act = toAction ocm prg
    ocm = checkOpCodeMap . solveConstraint . M.toList . buildOpCodeMap $ ts

toAction :: OpCodeMap -> [MInstr] -> Action ()
toAction ocm = sequence_ . map instr2action
  where
    instr2action (oc, (x1, x2, x3)) = ins x1 x2 x3
      where
        ins = maybe (error "wrong opcode") id $
              lookup (ocm M.! oc) allInstr

