type SP = Parsec () String

fromString :: String -> Input
fromString s = maybe (error "no parse") id $ parseMaybe pInput s

pInput :: SP Input
pInput = (,) <$> parse1 <*> (nl *> nl *> parse2)
  where
    parse1 :: SP Input1
    parse1 = many testCase

    parse2 :: SP Input2
    parse2 = many (minstr <* nl)

    testCase :: SP TestInstr
    testCase = do
      b <- string "Before: " *> list4 <* nl
      i <- minstr <* nl
      a <- string "After:  " *> list4 <* nl <* nl
      return (b, i, a)

    list4 :: SP [Val]
    list4 = between (char '[') (char ']') $ do
      x0 <- num <* ca
      x1 <- num <* ca
      x2 <- num <* ca
      x3 <- num
      return [x0, x1, x2, x3]

    minstr :: SP MInstr
    minstr = do
      oc <- num <* sp
      x1 <- num <* sp
      x2 <- num <* sp
      x3 <- num
      return (oc, (x1, x2, x3))

    num :: SP Int
    num = read <$> some digitChar

    nl = char '\n'
    ca = string ", "
    sp = char ' '

